package com.example.app.Repositories.dao;import com.example.app.domain.Message;import com.example.app.domain.User;import java.util.List;public interface MessageDao {    void addMessage(Message message);    Message getById(Long id);    List<Message> getByTag(String tag);    List<Message> getAll();    void update(Message message);    void delete(Message message);    List<Message> getMessagesByUser(User user);    List<Message> getSubMessagesByUsers(User user);    List<Message> getSubMessagesByUsersAndByTag(User user, String tag);    void like(User user, Message message);    void dislike(User user, Message message);}