package com.example.app.domain.dto;import com.example.app.domain.Message;import com.example.app.domain.User;import com.example.app.domain.util.MessageHelper;import java.math.BigInteger;public class MessageDto {    private Long id;    private String text;    private String tag;    private User author;    private Long author_id;    private String filename;    private Long likes;    private boolean meLiked;    public Long getId() {        return id;    }    public void setId(BigInteger id) {        this.id = Long.valueOf(String.valueOf(id));    }    public String getText() {        return text;    }    public void setText(String text) {        this.text = text;    }    public String getTag() {        return tag;    }    public void setTag(String tag) {        this.tag = tag;    }    public User getAuthor() {        return author;    }    public void setAuthor(User author) {        this.author = author;    }    public String getFilename() {        return filename;    }    public void setFilename(String filename) {        this.filename = filename;    }    public Long getLikes() {        return likes;    }    public void setLikes(BigInteger likes) {        this.likes =  Long.valueOf(String.valueOf(likes));    }    public boolean isMeLiked() {        return meLiked;    }    public void setMeLiked(boolean meLiked) {        this.meLiked = meLiked;    }    public Long getAuthor_id() {        return author_id;    }    public void setAuthor_id(BigInteger author_id) {        this.author_id =  Long.valueOf(String.valueOf(author_id));    }    public String getAuthorName(){        return MessageHelper.getAuthorName(author);    }}